let languageNotify=a=>{document.getElementById("lang-notification").innerText=a};function getLanguage(){return localStorage.getItem('language')?localStorage.getItem('language'):null}function setLanguage(a){localStorage.setItem('language',a)}function initLanguageController(){const a=getLanguage();if(a===null){const a=/^\/ja/g.exec(window.location.pathname),b=/^\/en/g.exec(window.location.pathname);a?setLanguage('ja'):b?setLanguage('en'):setLanguage('zh-cn')}}function getLocalizationNotFound(){getLanguage()||initLanguageController();var a="找不到该页面";switch(getLanguage()){case"ja":a="ページが見つかりません";break;case"en":a="Page not found"}return a}function getLocalizationReturn(){getLanguage()||initLanguageController();var a="返回上一页";switch(getLanguage()){case"ja":a="戻る";break;case"en":a="Go back"}return a}function getLocalizationHome(){getLanguage()||initLanguageController();var a="主页";switch(getLanguage()){case"ja":a="ホーム";break;case"en":a="Home"}return a}function getLocalizationHomeURL(){getLanguage()||initLanguageController();var a="/";switch(getLanguage()){case"ja":a="/ja/";break;case"en":a="/en/"}return a}